using Microsoft.AspNetCore.Mvc;
using PrimerCore.Models;
using System;
using System.Collections.Generic;
using System.Linq;
using System.Threading.Tasks;

namespace PrimerCore.Controllers
{
    public class PracticasController : Controller
    {
        public IActionResult Menu()
        {
            return View();
        }
        public IActionResult EjemploColecciones()
        {
            List<Comic> comics = new List<Comic>();
            for(int i = 1; i <= 10; i++)
            {
                Comic comic = new Comic();
                comic.Nombre = "Comic" + i;
                comic.Descripcion = "Descripcion" + i;
                comic.Imagen = "data:image/jpeg;base64,";
                comics.Add(comic);
            }
            return View(comics);
        }
        public IActionResult SumarNumerosPost()
        {
            return View();
        }
        [HttpPost]
        public IActionResult SumarNumerosPost(int numero1, int numero2)
        {
            int suma = numero1 + numero2;
            ViewBag.Suma = suma;
            return View();
        }
        public IActionResult SumarNumerosGet(int numero1, int numero2)
        {
            int suma = numero1 + numero2;
            ViewBag.Suma = suma;
            return View();
        }
    }
}
